ScrollView.script:
import org.itsnat.itsnatdroidtest.R;
import org.itsnat.itsnatdroidtest.testact.TestActivity;
import android.view.animation.*;
import android.content.res.Resources;

void initialConfiguration(TestActivity act, View rootView)
{
    initGridView(act, rootView);
    initInterpolatorTests(act, rootView);
}

void initGridView(TestActivity act, View rootView)
{
    Resources res = act.getResources();
    GridView gridView = (GridView) rootView.findViewById(R.id.gridViewAnimationTestId);
    CharSequence[] entries = res.getTextArray(R.array.sports_array);
    gridView.setAdapter(new ArrayAdapter(act, android.R.layout.simple_list_item_1, entries));
}

void initInterpolatorTests(TestActivity act, View rootView)
{
    initInterpolatorAccelerateTest(act,rootView);
    initInterpolatorAccelerateDecelerateTest(act,rootView);
    initInterpolatorAnticipateTest(act,rootView);
    initInterpolatorAnticipateOvershootTest(act,rootView);
    initInterpolatorBounceTest(act,rootView);
    initInterpolatorCycleTest(act,rootView);
    initInterpolatorDecelerateTest(act,rootView);
    initInterpolatorLinearTest(act,rootView);
    initInterpolatorOvershootTest(act,rootView);
}

TextView getTextView(View rootView,int id)
{
    return (TextView) rootView.findViewById(id);
}

Animation getAnimation(TestActivity act)
{
    return (TranslateAnimation) itsNatDoc.getItsNatResources().getAnimation("@remote:anim/droid/res/anim/test_animation_translate_for_interpolators_remote.xml");
}

Interpolator getInterpolator(String assetRef,TestActivity act)
{
    return itsNatDoc.getItsNatResources().getInterpolator(assetRef);
}

void initInterpolatorAccelerateTest(TestActivity act, View rootView)
{
    TextView textView = getTextView(rootView,R.id.acInterpolatorTestId1);
    Animation animation = getAnimation(act);

    AccelerateInterpolator interpolator = (AccelerateInterpolator)getInterpolator(
            "@remote:anim/droid/res/anim/test_interpolator_accelerate_remote.xml",act);

    animation.setInterpolator(interpolator);
    textView.startAnimation(animation);
}

void initInterpolatorAccelerateDecelerateTest(TestActivity act, View rootView)
